scriptSource.command:
import hudson.model.*
import hudson.node_monitors.*
import hudson.slaves.*
import java.util.concurrent.*

jenkins = Hudson.instance

// get current thread / Executor
def thr = Thread.currentThread()

// get current build
def build = thr?.executable


String REGEX = build.environment.get("Include_Regex")


now=Calendar.instance;

println("The build is run at ${now.time}");

for (item in jenkins.items){
   println("\t ${item.name}");
   // Ignore project that contains freeze or patch case insensitive
   if (item.name ==~ $Include_Regex){
       item.delete()
    }

}
